#! /usr/bin/env node
import inquirer from "inquirer";
console.log("Raed Hashmi's Caclculator");
async function Question() {
    inquirer.prompt([
        {
            name: "Operator",
            message: "Choose Operation: ",
            type: "list",
            choices: ["Add", new inquirer.Separator(), "Subtract", new inquirer.Separator(), "Multiply", new inquirer.Separator(), "Divide"]
        },
        {
            name: "num1",
            message: "Enter First Number ",
            type: "number"
        },
        {
            name: "num2",
            message: "Enter Second Number: ",
            type: "number"
        }
    ])
        .then((answers) => {
        if (answers.Operator == "Add") {
            console.log(`Answer of Add = ${answers.num1} + ${answers.num2} = ${answers.num1 + answers.num2}`);
        }
        else if (answers.Operator == "Subtract") {
            console.log(`Answer of Subtract = ${answers.num1} - ${answers.num2} = ${answers.num1 - answers.num2}`);
        }
        else if (answers.Operator == "Multiply") {
            console.log(`Answer of Multiply = ${answers.num1} * ${answers.num2} = ${answers.num1 * answers.num2}`);
        }
        else if (answers.Operator == "Divide") {
            console.log(`Answer of Divide = ${answers.num1} / ${answers.num2} = ${answers.num1 / answers.num2}`);
        }
    });
}
Question();
