#! /usr/bin/env node
import inquirer from 'inquirer';
async function question() {
    inquirer.prompt([
        {
            type: 'list',
            message: 'Selection Operation',
            name: 'opr',
            choices: ["Addition", new inquirer.Separator(), "Subtration", new inquirer.Separator(), "Multiplication", new inquirer.Separator(), "Division"],
        },
        {
            type: "input",
            message: "Enter first number",
            name: "num1"
        },
        {
            type: "input",
            message: "Second number",
            name: "num2"
        }
    ])
        .then((answers) => {
        if (answers.opr == "Addition") {
            console.log(`Answer of addion =  ${answers.num1} + ${answers.num2} = ${answers.num1 + answers.num2}`);
            console.log(`Answer of Addition operation is: ${answers.num1} + ${answers.num2} = ${answers.num1 + answers.num2}`);
        }
        else if (answers.opr == "Subtraction") {
            console.log(`Answer of subtraction ="${answers.num1} - ${answers.num2} = ${answers.num1 - answers.num2}`);
        }
        else if (answers.opr == "Multiplication") {
            console.log(`Answer of multiplication ="${answers.num1} * ${answers.num2} = ${answers.num1 * answers.num2}`);
        }
        else if (answers.opr == "Division") {
            console.log(`Answer of division ="${answers.num1} / ${answers.num2} = ${answers.num1 / answers.num2}`);
        }
    });
}
question();
